class StoreManager: NSObject, ObservableObject, SKProductsRequestDelegate, SKPaymentTransactionObserver {
    static let shared = StoreManager()

    @Published var coinsPurchased: [CoinType] = []

    let productIdentifiers: Set<String> = [
        "com.example.coins.gold",
        "com.example.coins.silver"
    ]

    var productsRequest: SKProductsRequest?
    var products: [SKProduct] = []

    override init() {
        super.init()
        SKPaymentQueue.default().add(self)
    }

    func fetchProducts() {
        productsRequest = SKProductsRequest(productIdentifiers: productIdentifiers)
        productsRequest?.delegate = self
        productsRequest?.start()
    }

    func buyCoins(_ coinType: CoinType) {
        guard let product = products.first(where: { $0.productIdentifier == getProductIdentifier(for: coinType) }) else {
            return
        }

        let payment = SKPayment(product: product)
        SKPaymentQueue.default().add(payment)
    }

    func restorePurchases() {
        SKPaymentQueue.default().restoreCompletedTransactions()
    }

    // MARK: - SKProductsRequestDelegate

    func productsRequest(_ request: SKProductsRequest, didReceive response: SKProductsResponse) {
        self.products = response.products
    }

    // MARK: - SKPaymentTransactionObserver

    func paymentQueue(_ queue: SKPaymentQueue, updatedTransactions transactions: [SKPaymentTransaction]) {
        for transaction in transactions {
            switch transaction.transactionState {
            case .purchased, .restored:
                handlePurchase(transaction)
            case .failed:
                handleFailedPurchase(transaction)
            default:
                break
            }
        }
    }

    // MARK: - Helper methods

    func getProductIdentifier(for coinType: CoinType) -> String {
        switch coinType {
        case .gold:
            return "com.example.coins.gold"
        case .silver:
            return "com.example.coins.silver"
        }
    }

    func handlePurchase(_ transaction: SKPaymentTransaction) {
        let purchasedCoin = CoinType(rawValue: getProductIdentifier(for: transaction.payment.productIdentifier))
        if let coinType = purchasedCoin {
            coinsPurchased.append(coinType)
        }

        SKPaymentQueue.default().finishTransaction(transaction)
    }

    func handleFailedPurchase(_ transaction: SKPaymentTransaction) {
        if let error = transaction.error {
            print("Purchase failed with error: \(error.localizedDescription)")
        }

        SKPaymentQueue.default().finishTransaction(transaction)
    }
}

struct ContentView: View {
    @ObservedObject var storeManager = StoreManager.shared

    var body: some View {
        VStack {
            ForEach(CoinType.allCases, id: \.self) { coinType in
                HStack {
                    Text("Buy \(coinType) coins")
                    Spacer()
                    Button(action: {
                        storeManager.buyCoins(coinType)
                    }) {
                        Text(coinType.getLocalizedPrice())
                    }
                    .padding(8)
                    .background(Color.blue)
                    .foregroundColor(.white)
                    .cornerRadius(8)
                }
                .padding()
            }

            Button(action: {
                storeManager.restorePurchases()
            }) {
                Text("Restore Purchases")
                    .foregroundColor(.blue)
            }
            .padding()
        }
        .onAppear {
            storeManager.fetchProducts()
        }
    }
}

